mapper.update:

update treasurebox
    set title=#{title},
        content=#{content}
where b_seq=#{b_seq}


update treasurebox
    set hit=hit+1
where b_seq=#{seq}


update treasurebox
    set is_deleted='Y'
where b_seq=#{seq}


update tbcomment
    set is_deleted='Y'
where b_seq=#{seq}


update tbcreply
    set is_deleted='Y'
where c_seq=#{c_seq}
